import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { setupAuth } from "./auth";
import { storage } from "./storage";
import { insertCourseSchema, insertAssignmentSchema, insertAnnouncementSchema, insertEmotionSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  setupAuth(app);

  // Middleware to check authentication
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Course routes
  app.get("/api/courses", requireAuth, async (req, res) => {
    try {
      const user = req.user;
      let courses;

      if (user.role === "teacher") {
        courses = await storage.getCoursesByTeacher(user.id);
      } else if (user.role === "student") {
        courses = await storage.getCoursesByStudent(user.id);
      } else {
        courses = [];
      }

      res.json(courses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch courses" });
    }
  });

  app.post("/api/courses", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can create courses" });
      }

      const courseData = insertCourseSchema.parse(req.body);
      const course = await storage.createCourse(courseData, req.user.id);

      // Broadcast new course to connected clients
      broadcastToClients({
        type: "new_course",
        data: course
      });

      res.status(201).json(course);
    } catch (error) {
      res.status(500).json({ message: "Failed to create course" });
    }
  });

  app.post("/api/courses/:id/enroll", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "student") {
        return res.status(403).json({ message: "Only students can enroll in courses" });
      }

      const courseId = req.params.id;
      const studentId = req.user.id;

      await storage.enrollStudent(courseId, studentId);

      res.status(201).json({ message: "Successfully enrolled in course", courseId, studentId });
    } catch (error) {
      res.status(500).json({ message: "Failed to enroll in course" });
    }
  });

  // Teacher analytics route
  app.get("/api/teacher/analytics", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can access analytics" });
      }

      const analytics = await storage.getTeacherAnalytics(req.user.id);
      res.json(analytics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch teacher analytics" });
    }
  });

  // Assignment routes
  app.get("/api/assignments", requireAuth, async (req, res) => {
    try {
      const user = req.user;

      if (user.role === "student") {
        const assignments = await storage.getAssignmentsByStudent(user.id);
        res.json(assignments);
      } else if (user.role === "teacher") {
        const assignments = await storage.getAssignmentsByTeacher(user.id);
        res.json(assignments);
      } else {
        res.json([]);
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch assignments" });
    }
  });

  app.post("/api/assignments", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can create assignments" });
      }

      console.log("[DEBUG] Assignment creation - Request body:", req.body);
      const assignmentData = insertAssignmentSchema.parse(req.body);
      console.log("[DEBUG] Assignment creation - Parsed data:", assignmentData);

      const assignment = await storage.createAssignment(assignmentData);
      console.log("[DEBUG] Assignment creation - Created assignment:", assignment);

      // Broadcast new assignment to connected clients
      broadcastToClients({
        type: "new_assignment",
        data: assignment
      });

      res.status(201).json(assignment);
    } catch (error) {
      console.error("[ERROR] Assignment creation failed:", error);
      res.status(500).json({ message: "Failed to create assignment" });
    }
  });

  app.post("/api/assignments/:id/submit", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "student") {
        return res.status(403).json({ message: "Only students can submit assignments" });
      }

      const submission = await storage.submitAssignment({
        assignmentId: req.params.id,
        studentId: req.user.id,
        content: req.body.content,
        attachments: req.body.attachments
      });

      // Broadcast new submission to relevant users only (specific teacher)
      try {
        // Get assignment details to find the specific teacher
        const assignment = await storage.getAssignmentById(req.params.id);
        if (!assignment) {
          console.error('Failed to find assignment for submission notification');
        } else {
          const teacherId = assignment.teacherId;
          const targetUsers = [teacherId]; // Only the specific teacher

          // Send only to the assignment's teacher to avoid privacy leaks
          broadcastToSpecificUsers({
            type: "new_submission",
            data: {
              submissionId: submission.id,
              assignmentId: req.params.id,
              studentId: req.user.id
            }
          }, targetUsers);

          console.log(`Submission notification sent for assignment ${req.params.id} to teacher ${teacherId}`);
        }
      } catch (error) {
        console.error('Failed to broadcast submission notification:', error);
      }

      res.status(201).json(submission);
    } catch (error) {
      res.status(500).json({ message: "Failed to submit assignment" });
    }
  });

  app.post("/api/assignments/:id/grade", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can grade assignments" });
      }

      const { submissionId, grade, feedback } = req.body;

      if (!submissionId || grade === undefined) {
        return res.status(400).json({ message: "Submission ID and grade are required" });
      }

      await storage.gradeAssignment(submissionId, grade, feedback || "");

      // Broadcast grade update to relevant users only (student and parents)
      try {
        // Get submission details to securely derive student ID
        const submission = await storage.getSubmissionById(submissionId);
        if (!submission) {
          console.error('Failed to find submission for grading notification');
          return;
        }

        const studentId = submission.studentId;
        const targetUsers = [studentId]; // Include the student

        // Send only to the graded student to avoid privacy leaks
        broadcastToSpecificUsers({
          type: "grade_updated",
          data: {
            submissionId,
            assignmentId: req.params.id,
            grade,
            feedback
          }
        }, targetUsers);

        console.log(`Grade notification sent for assignment ${req.params.id} to student ${studentId}`);
      } catch (error) {
        console.error('Failed to broadcast grade notification:', error);
      }

      res.json({ message: "Assignment graded successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to grade assignment" });
    }
  });

  // Announcement routes
  app.get("/api/announcements", requireAuth, async (req, res) => {
    try {
      const announcements = await storage.getAnnouncements(req.user.id);
      res.json(announcements);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch announcements" });
    }
  });

  app.post("/api/announcements", requireAuth, async (req, res) => {
    try {
      console.log("POST /api/announcements - User:", req.user.role);
      console.log("POST /api/announcements - Request body:", JSON.stringify(req.body, null, 2));

      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can create announcements" });
      }

      const announcementData = insertAnnouncementSchema.parse({
        ...req.body,
        authorId: req.user.id
      });

      console.log("POST /api/announcements - Parsed data:", JSON.stringify(announcementData, null, 2));

      const announcement = await storage.createAnnouncement(announcementData);

      console.log("POST /api/announcements - Created announcement:", JSON.stringify(announcement, null, 2));

      // Broadcast announcement to connected clients
      broadcastToClients({
        type: "new_announcement",
        data: announcement
      });

      res.status(201).json(announcement);
    } catch (error) {
      console.error("POST /api/announcements - Error:", error);
      res.status(500).json({ message: "Failed to create announcement" });
    }
  });

  // Timetable routes
  app.get("/api/timetable", requireAuth, async (req, res) => {
    try {
      const timetable = await storage.getTimetableByStudent(req.user.id);
      res.json(timetable);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch timetable" });
    }
  });

  app.post("/api/timetable", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "teacher") {
        return res.status(403).json({ message: "Only teachers can create timetable entries" });
      }

      const entry = await storage.createTimetableEntry(req.body);

      // Broadcast timetable update to connected clients
      broadcastToClients({
        type: "timetable_updated",
        data: entry
      });

      res.status(201).json(entry);
    } catch (error) {
      res.status(500).json({ message: "Failed to create timetable entry" });
    }
  });

  // Emotion routes
  app.get("/api/emotions", requireAuth, async (req, res) => {
    try {
      const emotions = await storage.getEmotionsByStudent(req.user.id);
      res.json(emotions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch emotions" });
    }
  });

  app.post("/api/emotions", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "student") {
        return res.status(403).json({ message: "Only students can record emotions" });
      }

      const emotionData = insertEmotionSchema.parse(req.body);
      const emotion = await storage.createEmotionEntry(emotionData, req.user.id);
      res.status(201).json(emotion);
    } catch (error) {
      res.status(500).json({ message: "Failed to record emotion" });
    }
  });

  // Parent routes
  app.get("/api/children", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "parent") {
        return res.status(403).json({ message: "Only parents can access this endpoint" });
      }

      const children = await storage.getChildrenByParent(req.user.id);
      res.json(children);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch children" });
    }
  });

  // Helper function to verify parent-child relationship
  async function verifyParentChildRelationship(parentId: string, childId: string): Promise<boolean> {
    const children = await storage.getChildrenByParent(parentId);
    return children.some(child => child.id === childId);
  }

  app.get("/api/children/:childId/assignments", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "parent") {
        return res.status(403).json({ message: "Only parents can access this endpoint" });
      }

      const { childId } = req.params;

      // Verify this child belongs to the parent
      const isValidChild = await verifyParentChildRelationship(req.user.id, childId);
      if (!isValidChild) {
        return res.status(403).json({ message: "You can only access your own children's data" });
      }

      const assignments = await storage.getAssignmentsByStudent(childId);
      res.json(assignments);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch child's assignments" });
    }
  });

  app.get("/api/children/:childId/courses", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "parent") {
        return res.status(403).json({ message: "Only parents can access this endpoint" });
      }

      const { childId } = req.params;

      // Verify this child belongs to the parent
      const isValidChild = await verifyParentChildRelationship(req.user.id, childId);
      if (!isValidChild) {
        return res.status(403).json({ message: "You can only access your own children's data" });
      }

      const courses = await storage.getCoursesByStudent(childId);
      res.json(courses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch child's courses" });
    }
  });

  app.get("/api/children/:childId/emotions", requireAuth, async (req, res) => {
    try {
      if (req.user.role !== "parent") {
        return res.status(403).json({ message: "Only parents can access this endpoint" });
      }

      const { childId } = req.params;

      // Verify this child belongs to the parent
      const isValidChild = await verifyParentChildRelationship(req.user.id, childId);
      if (!isValidChild) {
        return res.status(403).json({ message: "You can only access your own children's data" });
      }

      const emotions = await storage.getEmotionsByStudent(childId);
      res.json(emotions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch child's emotions" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket setup with user tracking
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });
  const clients = new Map<WebSocket, { userId: string; role: string }>();

  wss.on('connection', (ws) => {
    ws.on('message', (data) => {
      try {
        const message = JSON.parse(data.toString());
        if (message.type === 'auth') {
          // Store user info with WebSocket connection
          clients.set(ws, { userId: message.userId, role: message.role });
        }
      } catch (error) {
        console.error('WebSocket message parse error:', error);
      }
    });

    ws.on('close', () => {
      clients.delete(ws);
    });
  });

  // Broadcast functions
  function broadcastToClients(message: any) {
    const messageString = JSON.stringify(message);
    clients.forEach((userInfo, client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(messageString);
      }
    });
  }

  function broadcastToSpecificUsers(message: any, targetUserIds: string[]) {
    const messageString = JSON.stringify(message);
    clients.forEach((userInfo, client) => {
      if (client.readyState === WebSocket.OPEN && targetUserIds.includes(userInfo.userId)) {
        client.send(messageString);
      }
    });
  }

  function broadcastToRoles(message: any, targetRoles: string[]) {
    const messageString = JSON.stringify(message);
    clients.forEach((userInfo, client) => {
      if (client.readyState === WebSocket.OPEN && targetRoles.includes(userInfo.role)) {
        client.send(messageString);
      }
    });
  }

  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  courses, 
  assignments, 
  announcements, 
  courseEnrollments,
  assignmentSubmissions,
  timetableEntries,
  emotionEntries,
  notifications,
  parentChildren,
  courseMaterials,
  attendanceRecords,
  type User, 
  type InsertUser,
  type Course,
  type Assignment,
  type Announcement,
  type AssignmentSubmission,
  type TimetableEntry,
  type EmotionEntry
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, or, count, sql } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  // User management
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Course management
  getCoursesByTeacher(teacherId: string): Promise<Course[]>;
  getCoursesByStudent(studentId: string): Promise<Course[]>;
  createCourse(course: any, teacherId: string): Promise<Course>;
  enrollStudent(courseId: string, studentId: string): Promise<void>;

  // Assignment management
  getAssignmentsByCourse(courseId: string): Promise<Assignment[]>;
  getAssignmentsByStudent(studentId: string): Promise<any[]>;
  getAssignmentsByTeacher(teacherId: string): Promise<any[]>;
  createAssignment(assignment: any): Promise<Assignment>;
  submitAssignment(submission: any): Promise<AssignmentSubmission>;
  gradeAssignment(submissionId: string, grade: number, feedback: string): Promise<void>;

  // Announcements
  getAnnouncements(userId: string): Promise<Announcement[]>;
  createAnnouncement(announcement: any): Promise<Announcement>;

  // Timetable
  getTimetableByStudent(studentId: string): Promise<TimetableEntry[]>;
  createTimetableEntry(entry: any): Promise<TimetableEntry>;

  // Emotions
  getEmotionsByStudent(studentId: string): Promise<EmotionEntry[]>;
  createEmotionEntry(entry: any, studentId: string): Promise<EmotionEntry>;

  // Parent functionality
  getChildrenByParent(parentId: string): Promise<User[]>;
  linkParentChild(parentId: string, childId: string): Promise<void>;

  // Analytics
  getTeacherAnalytics(teacherId: string): Promise<{
    totalStudents: number;
    assignmentsGraded: number;
    averageGrade: number;
    recentActivity: any[];
  }>;

  sessionStore: session.SessionStore;
}

export class DatabaseStorage implements IStorage {
  sessionStore: session.SessionStore;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getCoursesByTeacher(teacherId: string): Promise<Course[]> {
    return await db
      .select()
      .from(courses)
      .where(eq(courses.teacherId, teacherId))
      .orderBy(desc(courses.createdAt));
  }

  async getCoursesByStudent(studentId: string): Promise<Course[]> {
    return await db
      .select({
        id: courses.id,
        title: courses.title,
        description: courses.description,
        teacherId: courses.teacherId,
        thumbnail: courses.thumbnail,
        isActive: courses.isActive,
        createdAt: courses.createdAt,
        updatedAt: courses.updatedAt,
        progress: courseEnrollments.progress,
      })
      .from(courses)
      .innerJoin(courseEnrollments, eq(courses.id, courseEnrollments.courseId))
      .where(eq(courseEnrollments.studentId, studentId))
      .orderBy(desc(courses.createdAt));
  }

  async createCourse(course: any, teacherId: string): Promise<Course> {
    const [newCourse] = await db
      .insert(courses)
      .values({ ...course, teacherId })
      .returning();
    return newCourse;
  }

  async enrollStudent(courseId: string, studentId: string): Promise<void> {
    // Check if student is already enrolled
    const existingEnrollment = await db
      .select()
      .from(courseEnrollments)
      .where(and(
        eq(courseEnrollments.courseId, courseId),
        eq(courseEnrollments.studentId, studentId)
      ))
      .limit(1);

    // If already enrolled, do nothing (idempotent operation)
    if (existingEnrollment.length > 0) {
      return;
    }

    // Otherwise, enroll the student
    await db
      .insert(courseEnrollments)
      .values({ courseId, studentId });
  }

  async getAssignmentsByCourse(courseId: string): Promise<Assignment[]> {
    return await db
      .select()
      .from(assignments)
      .where(eq(assignments.courseId, courseId))
      .orderBy(asc(assignments.dueDate));
  }

  async getAssignmentById(assignmentId: string): Promise<any> {
    const [assignment] = await db
      .select({
        id: assignments.id,
        title: assignments.title,
        courseId: assignments.courseId,
        teacherId: courses.teacherId,
        courseTitle: courses.title,
      })
      .from(assignments)
      .innerJoin(courses, eq(assignments.courseId, courses.id))
      .where(eq(assignments.id, assignmentId));
    return assignment;
  }

  async getSubmissionById(submissionId: string): Promise<any> {
    const [submission] = await db
      .select({
        id: assignmentSubmissions.id,
        assignmentId: assignmentSubmissions.assignmentId,
        studentId: assignmentSubmissions.studentId,
        grade: assignmentSubmissions.grade,
        status: assignmentSubmissions.status,
      })
      .from(assignmentSubmissions)
      .where(eq(assignmentSubmissions.id, submissionId));
    return submission;
  }

  async getAssignmentsByStudent(studentId: string): Promise<any[]> {
    return await db
      .select({
        id: assignments.id,
        title: assignments.title,
        description: assignments.description,
        dueDate: assignments.dueDate,
        maxPoints: assignments.maxPoints,
        courseTitle: courses.title,
        status: assignmentSubmissions.status,
        grade: assignmentSubmissions.grade,
        submittedAt: assignmentSubmissions.submittedAt,
      })
      .from(assignments)
      .innerJoin(courses, eq(assignments.courseId, courses.id))
      .innerJoin(courseEnrollments, eq(courses.id, courseEnrollments.courseId))
      .leftJoin(assignmentSubmissions, 
        and(
          eq(assignments.id, assignmentSubmissions.assignmentId),
          eq(assignmentSubmissions.studentId, studentId)
        )
      )
      .where(eq(courseEnrollments.studentId, studentId))
      .orderBy(asc(assignments.dueDate));
  }

  async getAssignmentsByTeacher(teacherId: string): Promise<any[]> {
    return await db
      .select({
        id: assignments.id,
        title: assignments.title,
        description: assignments.description,
        dueDate: assignments.dueDate,
        maxPoints: assignments.maxPoints,
        courseTitle: courses.title,
        courseId: courses.id,
        submissionCount: count(assignmentSubmissions.id),
        createdAt: assignments.createdAt,
      })
      .from(assignments)
      .innerJoin(courses, eq(assignments.courseId, courses.id))
      .leftJoin(assignmentSubmissions, eq(assignments.id, assignmentSubmissions.assignmentId))
      .where(eq(courses.teacherId, teacherId))
      .groupBy(assignments.id, courses.id, courses.title)
      .orderBy(desc(assignments.createdAt));
  }

  async createAssignment(assignment: any): Promise<Assignment> {
    const [newAssignment] = await db
      .insert(assignments)
      .values(assignment)
      .returning();
    return newAssignment;
  }

  async submitAssignment(submission: any): Promise<AssignmentSubmission> {
    const [newSubmission] = await db
      .insert(assignmentSubmissions)
      .values({ ...submission, status: "submitted" })
      .returning();
    return newSubmission;
  }

  async gradeAssignment(submissionId: string, grade: number, feedback: string): Promise<void> {
    await db
      .update(assignmentSubmissions)
      .set({ 
        grade, 
        feedback, 
        status: "graded",
        gradedAt: new Date()
      })
      .where(eq(assignmentSubmissions.id, submissionId));
  }

  async getAnnouncements(userId: string): Promise<Announcement[]> {
    // Get user's enrolled courses
    const userCourses = await db
      .select({ courseId: courseEnrollments.courseId })
      .from(courseEnrollments)
      .where(eq(courseEnrollments.studentId, userId));

    const courseIds = userCourses.map(c => c.courseId);

    return await db
      .select({
        id: announcements.id,
        title: announcements.title,
        content: announcements.content,
        authorId: announcements.authorId,
        courseId: announcements.courseId,
        isGlobal: announcements.isGlobal,
        createdAt: announcements.createdAt,
        authorName: users.firstName,
        courseName: courses.title,
      })
      .from(announcements)
      .innerJoin(users, eq(announcements.authorId, users.id))
      .leftJoin(courses, eq(announcements.courseId, courses.id))
      .where(
        or(
          eq(announcements.isGlobal, true),
          courseIds.length > 0 ? eq(announcements.courseId, courseIds[0]) : eq(announcements.isGlobal, true)
        )
      )
      .orderBy(desc(announcements.createdAt));
  }

  async createAnnouncement(announcement: any): Promise<Announcement> {
    const [newAnnouncement] = await db
      .insert(announcements)
      .values(announcement)
      .returning();
    return newAnnouncement;
  }

  async getTimetableByStudent(studentId: string): Promise<TimetableEntry[]> {
    return await db
      .select({
        id: timetableEntries.id,
        title: timetableEntries.title,
        dayOfWeek: timetableEntries.dayOfWeek,
        startTime: timetableEntries.startTime,
        endTime: timetableEntries.endTime,
        location: timetableEntries.location,
        courseTitle: courses.title,
        courseId: timetableEntries.courseId,
        createdAt: timetableEntries.createdAt,
      })
      .from(timetableEntries)
      .innerJoin(courses, eq(timetableEntries.courseId, courses.id))
      .innerJoin(courseEnrollments, eq(courses.id, courseEnrollments.courseId))
      .where(eq(courseEnrollments.studentId, studentId))
      .orderBy(asc(timetableEntries.dayOfWeek), asc(timetableEntries.startTime));
  }

  async createTimetableEntry(entry: any): Promise<TimetableEntry> {
    const [newEntry] = await db
      .insert(timetableEntries)
      .values(entry)
      .returning();
    return newEntry;
  }

  async getEmotionsByStudent(studentId: string): Promise<EmotionEntry[]> {
    return await db
      .select()
      .from(emotionEntries)
      .where(eq(emotionEntries.studentId, studentId))
      .orderBy(desc(emotionEntries.detectedAt))
      .limit(50);
  }

  async createEmotionEntry(entry: any, studentId: string): Promise<EmotionEntry> {
    const [newEntry] = await db
      .insert(emotionEntries)
      .values({ ...entry, studentId })
      .returning();
    return newEntry;
  }

  async getChildrenByParent(parentId: string): Promise<User[]> {
    return await db
      .select({
        id: users.id,
        username: users.username,
        email: users.email,
        firstName: users.firstName,
        lastName: users.lastName,
        role: users.role,
        profilePicture: users.profilePicture,
        createdAt: users.createdAt,
        updatedAt: users.updatedAt,
        password: users.password,
      })
      .from(users)
      .innerJoin(parentChildren, eq(users.id, parentChildren.childId))
      .where(eq(parentChildren.parentId, parentId));
  }

  async linkParentChild(parentId: string, childId: string): Promise<void> {
    await db
      .insert(parentChildren)
      .values({ parentId, childId });
  }

  async getTeacherAnalytics(teacherId: string): Promise<{
    totalStudents: number;
    assignmentsGraded: number;
    averageGrade: number;
    recentActivity: any[];
  }> {
    // Get courses taught by this teacher
    const teacherCourses = await db
      .select({ id: courses.id })
      .from(courses)
      .where(eq(courses.teacherId, teacherId));

    const courseIds = teacherCourses.map(course => course.id);

    if (courseIds.length === 0) {
      return {
        totalStudents: 0,
        assignmentsGraded: 0,
        averageGrade: 0,
        recentActivity: []
      };
    }

    // Count total students enrolled in teacher's courses
    const [studentsResult] = await db
      .select({ count: count() })
      .from(courseEnrollments)
      .where(courseIds.length === 1 
        ? eq(courseEnrollments.courseId, courseIds[0])
        : or(...courseIds.map(id => eq(courseEnrollments.courseId, id)))
      );

    // Get assignments created by this teacher and count graded ones
    const teacherAssignments = await db
      .select({ id: assignments.id })
      .from(assignments)
      .where(courseIds.length === 1 
        ? eq(assignments.courseId, courseIds[0])
        : or(...courseIds.map(id => eq(assignments.courseId, id)))
      );

    const assignmentIds = teacherAssignments.map(a => a.id);

    let assignmentsGraded = 0;
    let averageGrade = 0;

    if (assignmentIds.length > 0) {
      // Count graded assignments
      const [gradedResult] = await db
        .select({ count: count() })
        .from(assignmentSubmissions)
        .where(and(
          assignmentIds.length === 1 
            ? eq(assignmentSubmissions.assignmentId, assignmentIds[0])
            : or(...assignmentIds.map(id => eq(assignmentSubmissions.assignmentId, id))),
          sql`grade IS NOT NULL`
        ));

      assignmentsGraded = gradedResult.count;

      // Calculate average grade
      const gradeResults = await db
        .select({ grade: assignmentSubmissions.grade })
        .from(assignmentSubmissions)
        .where(and(
          assignmentIds.length === 1 
            ? eq(assignmentSubmissions.assignmentId, assignmentIds[0])
            : or(...assignmentIds.map(id => eq(assignmentSubmissions.assignmentId, id))),
          sql`grade IS NOT NULL`
        ));

      if (gradeResults.length > 0) {
        const totalGrade = gradeResults.reduce((sum, result) => sum + (result.grade || 0), 0);
        averageGrade = Math.round((totalGrade / gradeResults.length) * 10) / 10;
      }
    }

    // Get recent activity (recent submissions and grading)
    let recentActivity = [];
    if (assignmentIds.length > 0) {
      recentActivity = await db
        .select({
          id: assignmentSubmissions.id,
          type: sql`'submission'`.as('type'),
          assignmentTitle: assignments.title,
          studentName: sql`CONCAT(${users.firstName}, ' ', ${users.lastName})`.as('studentName'),
          timestamp: assignmentSubmissions.submittedAt,
          status: assignmentSubmissions.status
        })
        .from(assignmentSubmissions)
        .innerJoin(assignments, eq(assignmentSubmissions.assignmentId, assignments.id))
        .innerJoin(users, eq(assignmentSubmissions.studentId, users.id))
        .where(assignmentIds.length === 1 
          ? eq(assignments.id, assignmentIds[0])
          : or(...assignmentIds.map(id => eq(assignments.id, id)))
        )
        .orderBy(desc(assignmentSubmissions.submittedAt))
        .limit(10);
    }

    return {
      totalStudents: studentsResult.count,
      assignmentsGraded,
      averageGrade,
      recentActivity
    };
  }
}

export const storage = new DatabaseStorage();

